def readGz(f):
  for l in gzip.open(f):
    yield eval(l)

allHelpful = []
userHelpful = defaultdict(list)
itemHelpful = defaultdict(list)

for l in readGz("train.json.gz"):
  user,item = l['reviewerID'],l['itemID']
  allHelpful.append(l['helpful'])
  userHelpful[user].append(l['helpful'])
  itemHelpful[item].append(l['helpful'])

averageRate = sum([x['nHelpful'] for x in allHelpful]) * 1.0 / sum([x['outOf'] for x in allHelpful])
userRate = {}
for u in userHelpful:
  totalU = sum([x['outOf'] for x in userHelpful[u]])
  if totalU > 0:
    userRate[u] = sum([x['nHelpful'] for x in userHelpful[u]]) * 1.0 / totalU
  else:
    userRate[u] = averageRate

itemRate = {}
for i in itemHelpful:
  totalI = sum([x['outOf'] for x in itemHelpful[i]])
  if totalI > 0:
    itemRate[i] = sum([x['nHelpful'] for x in itemHelpful[i]]) * 1.0 / totalI
  else:
    itemRate[i] = averageRate

from collections import defaultdict

def readGz(f):
    for l in gzip.open(f):
        yield eval(l)

def parse(path):
    g = gzip.open(path, 'rb')
    for l in g:
        yield eval(l)

def getDF(path):
    i = 0
    df = {}
    for d in parse(path):
        df[i] = d
        i += 1
    return pd.DataFrame.from_dict(df, orient='index')

# Create first review time for each item - to be used for 'review_lateness' feature
first_item_review = {}
for item in df.itemID.unique():
  first_item_review[item] = min(df.unixReviewTime[df.itemID == item].to_list())
df['first_item_review'] = df.itemID.map(lambda item: first_item_review[item])

# Create average helpfulness for reviews for each item
ave_item_helpful = {}
for item in df.itemID.unique():
  ave_item_helpful[item] = np.average(df.perc_helpful[df.itemID == item].to_list())
df['ave_item_helpful'] = df.itemID.map(lambda item: ave_item_helpful[item])

